import React, { Component } from "react";
import { Button, Form, Checkbox } from "semantic-ui-react";

export default class RegisterUser extends Component {
  state = {
    name: "",
    password: "",
    email: "",
    phone: "",
  };

  saveName = (name) => {
    this.setState({ name: name });
  };
  savePassword = (password) => {
    this.setState({ password: password });
  };
  saveEmail = (email) => {
    this.setState({ email: email });
  };
  savePhone = (phone) => {
    this.setState({ phone: phone });
  };

  addUser = () => {};

  render() {
    return (
      <div>
        <Form>
          <Form.Field>
            <label>Name</label>
            <input placeholder="First Name" onChange={this.saveName(name)} />
          </Form.Field>
          <Form.Field>
            <label>Email</label>
            <input placeholder="Email" onChange={this.saveEmail(name)} />
          </Form.Field>
          <Form.Field>
            <label>Phone</label>
            <input placeholder="Phone" onChange={this.savePhone(name)} />
          </Form.Field>
          <Form.Field>
            <label>Password</label>
            <input placeholder="Password" onChange={this.savePassword(name)} />
          </Form.Field>
          <Form.Field>
            <Checkbox label="I agree to the Terms and Conditions" />
          </Form.Field>
          <Button type="submit" onClick={this.addUser}>
            Submit
          </Button>
        </Form>
      </div>
    );
  }
}
